mapper.update:
UPDATE Answer SET
answerContent = #{answerContent}
WHERE answerNum = #{answerNum}

UPDATE Answer SET
isSelected = 1
WHERE answerNum = #{answerNum}

mapper.select:
SELECT COUNT(*)
FROM Answer
WHERE boardNum = #{boardNum}

mapper.update:
UPDATE Answer
SET replyOrder = replyOrder + 1
WHERE replyGID = #{replyGID} and replyOrder > #{replyOrder}
